import styled from 'styled-components'

export const StyledButton = styled.button`
	background-color: blue;
	cursor: pointer;
	margin: 1rem 0;
`
export const StyledContent = styled.div`
	height: 26rem;
	width: 100%;
	display: flex;
	justify-content: space-between;
	margin: 2rem 2rem;

	StyledConten
	.collapsible .content-parent {
		height: 0px;
		overflow: hidden;
		transition: height ease 0.3s;
		border: black solid 1px;
		display: flex;
	}

	& .collapsible .content-parent.show {
		height: 30rem;
		width: 100%;
	}

	.content {
		display: flex;
		flex-direction: column;
		width: 50%;
		flex-wrap: wrap;
	}

	.MapStyle {
		height: 20rem;
		min-width: 50%;
	}

	& .company-text {
		max-width: 40%;
		margin: auto 0;
		> h1 {
			text-align: center;
		}
		> p {
			margin-top: 5rem;
		}
	}
`
